import React,{useState,useEffect} from "react";
import './AHome.css';
import axios from "axios";
import Select from "react-select";
import {Link,useNavigate} from 'react-router-dom';

function AHome(){
    const [cdata,setcdata]=useState([]);
    const [vchecker,setchecker]=useState(false);
    const [vchecker1,setchecker1]=useState(false);
    const [center_name,set_center_name]=useState('');
    const [center_location,set_center_location]=useState('');
    const [center_mobile,set_mobile]=useState('');
    const [center_dosage,set_dosage]=useState(0);
    const navigate=useNavigate()
    useEffect(()=>{
        axios.get('http://localhost:3001/acentersee').then((response)=>{
          if(response.data==="no"){
            setchecker1(true);
          }
          else{
          setcdata(response.data);
          setchecker(true);
          }
        });

        })
        const myinsert=()=>{
            axios.post('http://localhost:3001/acenterins',{
                cname:center_name,
                location:center_location,
                mobile:center_mobile,
                dosage:center_dosage
            }).then((response)=>{
                if(response.data==="no"){
                    alert('Vaccination Center already exists');    
                }
                else{
                alert('Vaccination Center created successfully');
                window.location.reload();
                navigate('/admin/home')

            }
            })
        }
        const mydelete=()=>{
            axios.post('http://localhost:3001/acenterdel',{
                cname:center_name
            }).then((response)=>{
                if(response.data==="no"){
                    alert('Error Occurred');    
                }
                else{
                alert('Vaccination Center deleted successfully');
                window.location.reload();
                navigate('/admin/home')
            }
            })
        }
        const options = cdata.map((data) => ({
            value: data.center_name,
            label: data.center_name,
          }));
    return(
        <div className="ahome">
            <h1>Vaccination Centers Chart</h1>

            <div className="admin-buttons">
                <button><i style={{marginRight:'3px'}}class="fa-solid fa-plus"></i><a href="#popup">Add</a></button>
                <button style={{backgroundColor:'red'}}><i style={{marginRight:'3px'}} class="fa-solid fa-trash"></i><a href="#popup1">Remove</a></button>
            </div>

            <div id="popup" class="overlay">
                <div class="popup">
                  <h2>CREATE VACCINATION CENTER</h2>
                  <a class="close" href="#">&times;</a>

                  <form onSubmit={myinsert}>
                    <div className="mydivs">
                        <label>Center Name</label>
                        <input type="text" placeholder="Enter center name" onChange={(e)=>{set_center_name(e.target.value);}}/>

                        <label>Center Location</label>
                        <input type="text" placeholder="Enter center location" onChange={(e)=>{set_center_location(e.target.value);}}/>

                        <label>Mobile Number</label>
                        <input type="tel" placeholder="Enter Contact Number" onChange={(e)=>{set_mobile(e.target.value);}}/>

                        <label>Dosage Availability</label>
                        <input type="number" placeholder="Enter dosage count" onChange={(e)=>{set_dosage(e.target.value);}}/>

                        <button type="submit">Submit</button>
                    </div>
                  </form>
                </div>
              </div>

              <div id="popup1" class="overlay">
                <div class="popup">
                  <h2>DELETE VACCINATION CENTER</h2>
                  <a class="close" href="#">&times;</a>
                    <h5>Note: Select the Center name to be deleted from the select box given below</h5>
                  <form onSubmit={mydelete}>
                    <div className="mydivs">
                        <label>Select Center Name</label>
                        <Select options={options} placeholder="Select Center Name" onChange={(e)=>{set_center_name(e.target.value);}}/>
                        <button type="submit">Submit</button>
                    </div>
                  </form>
                </div>
              </div>

            <div className="list-table">
            <table  class="table table-striped table-bordered table-sm" cellspacing="0" width="100%">
            <thead>
    <tr>
      <th class="th-sm">Centre Name
      </th>
      <th class="th-sm">Centre Location
      </th>
      <th class="th-sm">Date added
      </th>
      <th class="th-sm">Mobile Number
      </th>
      <th class="th-sm">Dosage Availability
      </th>

    </tr>
  </thead>
  <tbody>
    {
    vchecker&&cdata.map((data)=>{
        return(
          <tr>
            <td>{data.center_name}</td>
            <td>{data.location}</td>
            <td>{data.date_added}</td>
            <td>{data.mobile}</td>
            <td>{data.dosage}</td>
          </tr>
        );
    })
    }
    {vchecker1&&<tr><td colSpan={5}>No data found</td></tr>}
    </tbody>
            </table>
            </div>
        </div>
    )
}
export default AHome;
