import React, { useRef, useEffect, useState } from "react";
import "./Booking.css";
import $ from 'jquery';
import axios from "axios";
import { DataTable } from 'datatables.net';
import 'datatables.net-bs4/js/dataTables.bootstrap4';
import moment from "moment";

export default function UHome(){
  const dataTableRef = useRef(null);
  const [bdata,setbdata]=useState([]);
  const [vchecker,setchecker]=useState(false);
  const [vchecker1,setchecker1]=useState(false);


    useEffect(()=>{
      axios.get('http://localhost:3001/ucentersee').then((response)=>{
        if(response.data==="no"){
          setchecker1(true);
        }
        else{
        setbdata(response.data);
        setchecker(true);
        }
      });

      })

        useEffect(() => {
          if(vchecker){
          $(dataTableRef.current).DataTable();
          $('.dataTables_length').addClass('bs-select');
          }
        }, [vchecker]);
        useEffect(() => {
          if(vchecker1){
          $(dataTableRef.current).DataTable();
          $('.dataTables_length').addClass('bs-select');
          }
        }, [vchecker1]);



    return(
        <div className="uu">
        <div className="ubooking">
            <table ref={dataTableRef} id="dtBasicExample" class="table table-striped table-bordered table-sm" cellspacing="0" width="100%">
  <thead>
    <tr>
      <th class="th-sm">Centre Name
      </th>
      <th class="th-sm">Location
      </th>
      <th class="th-sm">Contact number
      </th>
      <th class="th-sm">Slot Availability
      </th>
      <th class="th-sm">Date
      </th>
      <th class="th-sm">Book Now
      </th>
    </tr>
  </thead>
  <tbody>
    {
      vchecker&&bdata.map((data)=>{
        return(
          <tr>
            <td>{data.center_name}</td>
            <td>{data.location}</td>
            <td>{data.mobile}</td>
            <td>{data.slot_available}</td>
            <td>{moment().format("DD-MM-YYYY")}</td>
            <td><button className="book-now">Book Now</button></td>
          </tr>
        );
      })
    }

  </tbody>
  <tfoot>
    <tr>
      <th>Centre Name
      </th>
      <th>Slot time
      </th>
      <th>Booking Date
      </th>
      <th>Aadhar Number
      </th>
      <th>Benificiary Name
      </th>
      <th>Benificiary Age
      </th>
    </tr>
  </tfoot>
</table>
        </div>
        </div>
    )
};
