import React,{useState,useEffect} from "react";
import './Login.css';
import axios from "axios";
import {Link,useNavigate} from 'react-router-dom';
import createBrowserHistory from 'history/createBrowserHistory'

const history = createBrowserHistory();
function Login(){
  const [mail,setmail]=useState('');
  const [pwd,setpwd]=useState('');
  const navigate=useNavigate();
  useEffect(()=>{
    window.addEventListener('popstate', (e) => {
      window.history.go(1);
    });
  }, []);


  const lcheck = (e)=>{
    e.preventDefault();
      axios.post("http://localhost:3001/login",{
        umail:mail,
        upwd:pwd,
      }).then((response)=>{
        if(response.data==="no"){ alert("Invalid Username/Password") }
        else{if(response.data[0].category==="user"){
          localStorage.setItem('username',mail);
          navigate("/user/home")
        }
        else if(response.data[0].category==="admin"){
          alert("You are logged in as Admin")
          localStorage.setItem('username',mail);
            navigate("/admin/home");

          }
        else if(response.data[0].category==="userv"){
            navigate("/register")}
        else{
          alert("Invalid Username/Password")
        }}
      });

  }
  return(
    <div className="body1">
      <div className="box-form">
        <div className="left">

          <div className="overlay">
            <h1>Let's Login</h1>
            <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.
              Curabitur et est sed felis aliquet sollicitudin</p>
            <span>


              <Link to="/register">
                <div>
                  Register
                </div>
              </Link>
            </span>
          </div>
        </div>


        <div className="right">

          <h4>Login</h4>
          <p>Let's Log you in..It takes less than a minute</p>
          <form onSubmit={lcheck}>
            <div className="inputs">

              <input type="email" placeholder="Enter Your mail" onChange={(e)=>{
                setmail(e.target.value);
              }}/>
              <input style={{marginBottom:'30px'}} type="password" placeholder="Enter Password" onChange={(e)=>{
                setpwd(e.target.value);
              }}/>

            </div>
            <button type="submit">Login</button>
          </form>

        </div>

      </div>
    </div>
  )

}

export default Login;
