import React, { Component } from "react";
import "./Login.css";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";

class Register extends Component {
  constructor(props) {
    super(props);
    this.state = {
      mail: "",
      pwd: "",
      cpwd: "",

    };

  }

  register = () => {
    const { cpwd, pwd, mail,iden } = this.state;
    if (cpwd !== pwd) {
      alert("Password and Confirm Password does not matches");
    } else {
        axios
          .post("http://localhost:3001/uregister", {
            umail:mail,
            upwd:pwd,
          })
          .then((response) => {
            if(response.data==="no"){
                alert("Username already exists");
                const navigate=useNavigate()
                navigate('/');
            }
            else{
            alert("Registeration mail sent");
            const navigate=useNavigate()
            navigate('/otp');
            }
          });

    }
  };

  render() {
    const { mail, pwd, cpwd } = this.state;

    return (
      <div>
        <div className="body1">
          <div class="box-form">
            <div class="left">
              <div class="overlay">
                <h1>Let's Register</h1>
                <p>
                  Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                  Curabitur et est sed felis aliquet sollicitudin
                </p>
                <span>
                  <Link to="/">
                    <div>Login</div>
                  </Link>
                </span>
              </div>
            </div>

            <div class="right">
              <h4>Register</h4>
              <p>Let's Register you in..It takes less than a minute</p>

              <form onSubmit={this.register}>
                <div class="inputs">
                  <input
                    type="email"
                    placeholder="Enter Your mail"
                    onChange={(e) => {
                      this.setState({ mail: e.target.value });
                    }}
                    value={mail}
                    required
                  />
                  <input
                    type="password"
                    placeholder="Enter Password"
                    onChange={(e) => {
                      this.setState({ pwd: e.target.value });
                    }}
                    value={pwd}
                    required
                  />
                  <input
                    type="password"
                    placeholder="Confirm Password"
                    onChange={(e) => {
                      this.setState({ cpwd: e.target.value });
                    }}
                    value={cpwd}
                    required
                  />
                </div>
                <button style={{ marginTop: "30px" }} type="submit">
                  Register
                </button>
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Register;
