import React, { useRef, useEffect, useState } from "react";
import "./Booking.css";
import $ from 'jquery';
import axios from "axios";
import { DataTable } from 'datatables.net';
import 'datatables.net-bs4/js/dataTables.bootstrap4';
import moment from "moment";

export default function UHome(){
  const dataTableRef = useRef(null);
  const [bdata,setbdata]=useState([]);
  const [vchecker,setchecker]=useState(false);
  const [vchecker1,setchecker1]=useState(false);
  const [bookcheck,set_book_check]=useState('');
  const[bname,set_bname]=useState('');
  const [bage,set_bage]=useState(0)
  const [aadhar,set_aadhar]=useState('');

    useEffect(()=>{
      axios.get('http://localhost:3001/ucentersee').then((response)=>{
        if(response.data==="no"){
          setchecker1(true);
        }
        else{
        setbdata(response.data);
        setchecker(true);
        }
      });      
      })

        useEffect(() => {
          if(vchecker){
          $(dataTableRef.current).DataTable();
          $('.dataTables_length').addClass('bs-select');
          }
        }, [vchecker]);
        useEffect(() => {
          if(vchecker1){
          $(dataTableRef.current).DataTable();
          $('.dataTables_length').addClass('bs-select');
          }
        }, [vchecker1]);



    return(
        <div className="uu">
        <div className="ubooking">
            <table ref={dataTableRef} id="dtBasicExample" class="table table-striped table-bordered table-sm" cellspacing="0" width="100%">
  <thead>
    <tr>
      <th class="th-sm">Centre Name
      </th>
      <th class="th-sm">Location
      </th>
      <th class="th-sm">Contact number
      </th>
      <th class="th-sm">Slot Availability
      </th>
      <th class="th-sm">Date
      </th>
      <th class="th-sm">Book Now
      </th>
    </tr>
  </thead>
  <tbody>
    {
      vchecker&&bdata.map((data)=>{
        if(data.slot_available>0){
            return(
                <tr>
                  <td>{data.center_name}</td>
                  <td>{data.location}</td>
                  <td>{data.mobile}</td>
                  <td>{data.slot_available}</td>
                  <td>{moment().format("DD-MM-YYYY")}</td>
                  <td><button className="book-now" onClick={set_book_check(data.center_name)}>Book Now</button></td>
                </tr>
              );
        }
        else{
            return(
                <tr>
                  <td>{data.center_name}</td>
                  <td>{data.location}</td>
                  <td>{data.mobile}</td>
                  <td>{data.slot_available}</td>
                  <td>{moment().format("DD-MM-YYYY")}</td>
                  <td><button className="book-now1">Book Now</button></td>
                </tr>
              );
        }
      })
    }
    {vchecker1&&<tr><td colSpan={5}>No data found</td></tr>}
  </tbody>
  <tfoot>
  <tr>
      <th>Centre Name
      </th>
      <th>Location
      </th>
      <th>Contact number
      </th>
      <th>Slot Availability
      </th>
      <th>Date
      </th>
      <th>Book Now
      </th>
    </tr>
  </tfoot>
</table>
        </div>
        <div id="popup" class="overlay">
                <div class="popup">
                  <h2>BOOK VACCINE</h2>
                  <a class="close" href="#">&times;</a>

                  <form onSubmit={bookinsert}>
                    <div className="mydivs">
                        <label>Center Name</label>
                        <input type="text" placeholder="Enter Center name" value={bookcheck} readOnly/>

                        <label>Benificiary Name</label>
                        <input type="text" placeholder="Enter Beneficiary name"  onChange={(e)=>{set_bname(e.target.value);}}/>

                        <label>Beneficiary Age</label>
                        <input type="number" placeholder="Enter Beneficiary Age" onChange={(e)=>{set_bage(e.target.value);}}/>

                        <label>Aadhar Number</label>
                        <input type="text" placeholder="Enter Aadhar Number" minLength={12} maxLength={12} pattern="[0-9]+" onChange={(e)=>{set_aadhar(e.target.value);}}/>

                        <label>Select Vaccine</label>
                        <select>
                            <option value={0} selected disabled>Select Vaccine</option>
                            {
                                bdata.map((data)=>{
                                    if(data.center_name===bookcheck){
                                    if(data.dosage>0){ return( <option value="cshield">Covishield</option> ) }
                                    if(data.covaxine>0){ return( <option value="cshield">Covaxin</option> ) }
                                    }
                                })
                            }                      
                        </select>

                        <button type="submit">Submit</button>
                    </div>
                  </form>
                </div>
              </div>
        </div>
    )
};
