import React,{useState,useEffect} from "react";
import './AHome.css';
import axios from "axios";
import {Link,useNavigate} from 'react-router-dom';

export default function AHome(){
    const [cdata,setcdata]=useState([]);
    const [vchecker,setchecker]=useState(false);
    const [vchecker1,setchecker1]=useState(false);
    useEffect(()=>{
        axios.post('http://localhost:3001/acentersee',{
           uname:localStorage.getItem('username').toString()
        }).then((response)=>{
          if(response.data==="no"){
            setchecker1(true);
          }
          else{
          setcdata(response.data);
          setchecker(true);
          }
        });

        })
    return(
        <div className="ahome">
            <h1>Vaccination Centers Chart</h1>

            <div className="admin-buttons">
                <button><i style={{marginRight:'3px'}}class="fa-solid fa-plus"></i>Add</button>
                <button style={{backgroundColor:'red'}}><i style={{marginRight:'3px'}} class="fa-solid fa-trash"></i>Remove</button>
            </div>

            <div className="list-table">
            <table  class="table table-striped table-bordered table-sm" cellspacing="0" width="100%">
            <thead>
    <tr>
      <th class="th-sm">Centre Name
      </th>
      <th class="th-sm">Centre Location
      </th>
      <th class="th-sm">Date added
      </th>
      <th class="th-sm">Mobile Number
      </th>
      <th class="th-sm">Dosage Availability
      </th>
    </tr>
  </thead>
  <tbody>
    {
    vchecker&&cdata.map((data)=>{
        return(
          <tr>
            <td>{data.center_name}</td>
            <td>{data.location}</td>
            <td>{data.date_added}</td>
            <td>{data.mobile}</td>
            <td>{data.dosage}</td>
          </tr>
        );
    })
    }
    {vchecker1&&<tr><td colSpan={5}>No data found</td></tr>}
    </tbody>
            </table>
            </div>
        </div>
    )
}
